import React from 'react';

let playersData = [

];


const Header = (props) => {
    return (
        <header>
            <h1>{props.title}</h1>
            <span className="stats">Players: {props.totalPlayers}</span>
        </header>

    );
}



class Counter extends React.Component {

    state = {
        score: 0
    };

    incrementScore() {
        // console.log('Hi from inside incrementScore!');
        this.setState( prevState => ({
            score: prevState.score + 1
        }));
    }

    decrementScore() {
        // console.log('Hi from inside decrementScore!');
        this.setState( prevState => ({
            score: prevState.score - 1 
        }));
    }


    render() {
        return (
            <div className="counter">
                <button className="counter-action decrement" onClick={ () => this.decrementScore() }> - </button>
                <span className="counter-score">{ this.state.score }</span>
                <button className="counter-action decrement" onClick={ () => this.incrementScore() }> + </button>
            </div>
        );
    } 
}


const Player = (props) => {
    return (
        <div className="player">
            <span className="player-name">
            <button className="remove-player" onClick={ () => props.removePlayer(props.id) } >
            x</button>
                {props.name}
            </span>
            <Counter score={props.score}/>
        </div>
    );
}

class App extends React.Component {

    state = {
        players: [
            {
                name: "Andrei",
                id: 1
              },
              {
                name: "Treasure",
                id: 2
              },
              {
                name: "Ashley",
                id: 3
              },
              {
                name: "James",
                id: 4
              }
        ]
    };

    handleRemovePlayer = (id) => {
        this.setState( prevState => {
            return {
                players: prevState.players.filter( p => p.id != id )
            };

        });
    }

    render() {
        return (
            <div className="scoreboard">
                <Header 
                    title="Scoreboard"
                    totalPlayers={this.state.players.length} />

                {/* Players list  */}
                {this.state.players.map( player => 

                <Player 
                    id={player.id}
                    name={player.name} 
                    key={player.id.toString()}
                    removePlayer={this.handleRemovePlayer}
                    />
                )}
            </div>

        );
    }

}





ReactDOM.render(
    <App initialPlayers={playersData} />,

    document.getElementById('root')
);
